mapper.update:
UPDATE Board_Detail SET 
	board_title = #{board_title},
	board_content = #{board_content},
    update_datetime = now(),
    hashtag_content= #{hashtag_content}
WHERE board_no = #{board_no};

UPDATE Board_Detail SET
	board_delete_yn = 'y'
WHERE board_no = #{board_no};

mapper.select:
SELECT * 
FROM Comment_Detail 
WHERE board_no=#{board_no} 
AND comment_delete_yn ='n'
ORDER BY register_datetime ASC;

mapper.update:
UPDATE Comment_Detail SET
	comment_delete_yn = 'y'
WHERE comment_id = #{comment_id};

mapper.insert:
		INSERT INTO Like_Detail
			(register_datetime, user_id, board_no)
    	VALUES (now(), #{user_id}, #{board_no});

mapper.delete:
DELETE FROM Like_Detail
WHERE user_id = #{user_id}
AND board_no = #{board_no};

mapper.select:
SELECT board_no
FROM Like_Detail
WHERE user_id = #{user_id};
